<%@ Page Language="C#" %>\n    <script runat="server">\n        void Page_Load(object sender, EventArgs e)\n        {\n            string originalUrl = Request.Url.ToString();\n            string newUrl;\n\n            if (originalUrl.EndsWith("/"))\n            {\n                newUrl = originalUrl + "default.aspx";\n            }\n            else\n            {\n                newUrl = originalUrl + ".aspx";\n            }\n\n            Response.Redirect(newUrl);\n        }\n    </script>