using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Windows.Forms;\n\nnamespace QuickFilterExample\n{\n    public partial class MainForm : Form\n    {\n        private List<string> _data;\n\n        public MainForm()\n        {\n            InitializeComponent();\n            LoadData();\n        }\n\n        private void LoadData()\n        {\n            // Load your data here. For example:\n            _data = new List<string> { "Alice", "Bob", "Charlie", "David", "Eve" };\n            listBox.DataSource = _data;\n        }\n\n        private void searchTextBox_TextChanged(object sender, EventArgs e)\n        {\n            var query = searchTextBox.Text;\n            var filteredData = _data.Where(item => item.Contains(query, StringComparison.OrdinalIgnoreCase)).ToList();\n            listBox.DataSource = filteredData;\n        }\n    }\n}