using System;\nusing System.ComponentModel.DataAnnotations;\n\n[MetadataType(typeof(User.Metadata))]\npublic class User\n{\n    #region Metadata\n    private class Metadata\n    {\n        [Required(ErrorMessage="Name is required")]\n        [StringLength(5, ErrorMessage="Max Length is 5")]\n        public string Name { get; set; }\n\n        [CustomValidation(typeof(User), "ValidateBirthDay", ErrorMessage="We have trouble.")]\n        public DateTime? BirthDay { get; set; }\n    }\n    #endregion\n\n    public static ValidationResult ValidateBirthDay(object value, ValidationContext context)\n    {\n        DateTime? birthDay = value as DateTime?;\n        if (birthDay != null)\n        {\n            // Add your custom validation logic here\n            if (birthDay > DateTime.Now)\n            {\n                return new ValidationResult("BirthDay cannot be in the future.");\n            }\n        }\n\n        return ValidationResult.Success;\n    }\n\n    public int? ID { get; set; }\n    public string Name { get; set; }\n    public DateTime? BirthDay { get; set; }\n}