<%@ Page Language="C#" AutoEventWireup="true" CodeBehind="EmailValidation.aspx.cs" Inherits="YourNamespace.EmailValidation" %>\n   <!DOCTYPE html>\n\n   <html xmlns="http://www.w3.org/1999/xhtml">\n   <head runat="server">\n       <title>Email Validation</title>\n       <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.5.1/jquery.min.js"></script>\n       <script type="text/javascript">\n           $(document).ready(function () {\n               $("#btnValidate").click(function () {\n                   var email = $("#txtEmail").val();\n                   $.ajax({\n                       type: "POST",\n                       url: "EmailValidation.aspx/ValidateEmail",\n                       data: JSON.stringify({ email: email }),\n                       contentType: "application/json; charset=utf-8",\n                       dataType: "json",\n                       success: function (response) {\n                           if (response.d) {\n                               $("#lblMessage").text("Email is valid.");\n                           } else {\n                               $("#lblMessage").text("Email is invalid.");\n                           }\n                       },\n                       failure: function (response) {\n                           $("#lblMessage").text("Error occurred.");\n                       }\n                   });\n               });\n           });\n       </script>\n   </head>\n   <body>\n       <form id="form1" runat="server">\n           <div>\n               <label for="txtEmail">Email:</label>\n               <input type="text" id="txtEmail" />\n               <input type="button" id="btnValidate" value="Validate" />\n               <label id="lblMessage"></label>\n           </div>\n       </form>\n   </body>\n   </html>