// Define an interface for instance methods\npublic interface IMyInterface\n{\n    void InstanceMethod();\n}\n\n// Define a static helper class with the required static methods\npublic static class MyInterfaceHelper\n{\n    public static void StaticMethod(IMyInterface instance)\n    {\n        // Implement the static method logic\n        Console.WriteLine("Static method called");\n        instance.InstanceMethod();\n    }\n}\n\n// Implement the interface in a class\npublic class MyClass : IMyInterface\n{\n    public void InstanceMethod()\n    {\n        Console.WriteLine("Instance method called");\n    }\n}\n\n// Example usage\npublic class Program\n{\n    public static void Main()\n    {\n        MyClass myObject = new MyClass();\n        MyInterfaceHelper.StaticMethod(myObject);\n    }\n}