using (var context = new AppDbContext())\n{\n    var bank = new Bank { Name = "Sample Bank" };\n    var country = new Country { Name = "Sample Country" };\n\n    context.Banks.Add(bank);\n    context.Countries.Add(country);\n    context.SaveChanges();\n\n    var billingReference = new BillingReference\n    {\n        ReferencingType = 5, // Bank\n        ReferencingId = bank.Id,\n        ReferencedType = 2, // Country\n        ReferencedId = country.Id,\n        IsActive = true\n    };\n\n    context.BillingReferences.Add(billingReference);\n    context.SaveChanges();\n}