using System;\nusing System.Data;\n\nnamespace ResetPrimaryKeyExample\n{\n    class Program\n    {\n        static MyDataSet sharedDS = new MyDataSet();\n\n        static void Main(string[] args)\n        {\n            // Example to populate the dataset initially\n            InitializeDataSet();\n\n            // Call the method to reset the dataset and primary key\n            CleanDS();\n\n            // Verify the reset\n            foreach (DataRow row in sharedDS.Table1.Rows)\n            {\n                Console.WriteLine(row["Column1"]);\n            }\n        }\n\n        static void InitializeDataSet()\n        {\n            sharedDS.Table1.Rows.Add(1, "Data1");\n            sharedDS.Table1.Rows.Add(2, "Data2");\n        }\n\n        static void CleanDS()\n        {\n            // Clear the dataset\n            sharedDS.Table1.Clear();\n\n            // Reset the primary key\n            sharedDS.Table1.Columns["Column1"].AutoIncrement = true;\n            sharedDS.Table1.Columns["Column1"].AutoIncrementSeed = 1;\n            sharedDS.Table1.Columns["Column1"].AutoIncrementStep = 1;\n\n            // Optionally, you can merge a reference dataset if needed\n            MyDataSet referenceDS = new MyDataSet();\n            sharedDS.Merge(referenceDS);\n        }\n    }\n\n    public class MyDataSet : DataSet\n    {\n        public DataTable Table1 { get; set; }\n\n        public MyDataSet()\n        {\n            Table1 = new DataTable("Table1");\n            DataColumn column1 = new DataColumn("Column1", typeof(int))\n            {\n                AutoIncrement = true,\n                AutoIncrementSeed = 1,\n                AutoIncrementStep = 1\n            };\n            Table1.Columns.Add(column1);\n            Table1.Columns.Add("Column2", typeof(string));\n            Tables.Add(Table1);\n        }\n    }\n}