using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = "path/to/your/file.txt";\n        int position = 100; // The position where you want to insert characters.\n        string insertText = "your text";\n\n        InsertTextInFile(filePath, position, insertText);\n    }\n\n    static void InsertTextInFile(string filePath, int position, string insertText)\n    {\n        string tempFile = Path.GetTempFileName();\n\n        using (var input = new FileStream(filePath, FileMode.Open, FileAccess.Read))\n        using (var output = new FileStream(tempFile, FileMode.Create, FileAccess.Write))\n        {\n            byte[] buffer = new byte[8192];\n            int bytesRead;\n\n            // Copy the part before the insertion point\n            for (int i = 0; i < position;)\n            {\n                bytesRead = input.Read(buffer, 0, Math.Min(buffer.Length, position - i));\n                output.Write(buffer, 0, bytesRead);\n                i += bytesRead;\n            }\n\n            // Write the text to be inserted\n            byte[] insertBytes = System.Text.Encoding.UTF8.GetBytes(insertText);\n            output.Write(insertBytes, 0, insertBytes.Length);\n\n            // Copy the remaining part of the file\n            while ((bytesRead = input.Read(buffer, 0, buffer.Length)) > 0)\n            {\n                output.Write(buffer, 0, bytesRead);\n            }\n        }\n\n        // Replace the original file with the modified file\n        File.Delete(filePath);\n        File.Move(tempFile, filePath);\n    }\n}