using System;\nusing System.Collections.Generic;\nusing System.Configuration;\n\nclass Program\n{\n    static void Main()\n    {\n        // Example configuration loaded from a file or other source\n        var configCases = new Dictionary<string, Action>\n        {\n            { "Case1", Case1 },\n            { "Case2", Case2 },\n            { "Case3", Case3 }\n        };\n\n        // Simulating configuration value read at runtime\n        string configValue = ConfigurationManager.AppSettings["SwitchCase"];\n\n        if (configCases.TryGetValue(configValue, out Action action))\n        {\n            action.Invoke();\n        }\n        else\n        {\n            Console.WriteLine("No matching case found.");\n        }\n    }\n\n    static void Case1()\n    {\n        Console.WriteLine("Executing Case 1");\n    }\n\n    static void Case2()\n    {\n        Console.WriteLine("Executing Case 2");\n    }\n\n    static void Case3()\n    {\n        Console.WriteLine("Executing Case 3");\n    }\n}