using System;\n\npublic partial class Default : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // This is just to demonstrate setting the hidden field value.\n        // Replace this with your actual date range check logic.\n        if (!IsPostBack)\n        {\n            hiddenFieldWarning.Value = "false";\n        }\n    }\n\n    protected void btnSearch_Click(object sender, EventArgs e)\n    {\n        // Replace this with your actual date range check logic.\n        DateTime startDate = DateTime.Now.AddDays(-30); // example start date\n        DateTime endDate = DateTime.Now; // example end date\n\n        if ((endDate - startDate).TotalDays > 21)\n        {\n            hiddenFieldWarning.Value = "true";\n        }\n        else\n        {\n            hiddenFieldWarning.Value = "false";\n        }\n    }\n}