using System;\nusing System.Net.Mail;\nusing System.IO;\n\npublic class EmailSender\n{\n    private static int emailsSent = 0;\n    private static string logFile = "email_log.txt";\n    private static int batchSize = 1000;\n    private static int delay = 90000; // 90 seconds\n\n    public static void Main()\n    {\n        int totalEmails = 25000;\n        string[] emailList = new string[totalEmails]; // Populate this with actual email addresses\n\n        LoadProgress();\n\n        for (int i = emailsSent; i < totalEmails; i++)\n        {\n            try\n            {\n                SendEmail(emailList[i]);\n                emailsSent++;\n\n                // Log progress\n                File.WriteAllText(logFile, emailsSent.ToString());\n\n                // Throttle\n                if (emailsSent % batchSize == 0)\n                {\n                    System.Threading.Thread.Sleep(delay);\n                }\n            }\n            catch (Exception ex)\n            {\n                // Handle exception (log it or notify the admin)\n                Console.WriteLine($"Error sending email to {emailList[i]}: {ex.Message}");\n                break;\n            }\n        }\n    }\n\n    private static void SendEmail(string recipient)\n    {\n        MailMessage mail = new MailMessage("your_email@example.com", recipient);\n        mail.Subject = "Subject";\n        mail.Body = "Email body";\n\n        SmtpClient client = new SmtpClient("smtp.example.com");\n        client.Send(mail);\n    }\n\n    private static void LoadProgress()\n    {\n        if (File.Exists(logFile))\n        {\n            emailsSent = int.Parse(File.ReadAllText(logFile));\n        }\n    }\n}