using System;\n\nclass Program\n{\n    static void Main()\n    {\n        // Example 10x10 matrix\n        int[,] matrix = new int[10, 10];\n        // Populate the matrix with some values for demonstration\n        for (int i = 0; i < 10; i++)\n        {\n            for (int j = 0; j < 10; j++)\n            {\n                matrix[i, j] = i * 10 + j;\n            }\n        }\n\n        // Define the size of the sliding window\n        int windowSize = 3;\n\n        // Iterate through the matrix with the sliding window\n        for (int i = 0; i <= matrix.GetLength(0) - windowSize; i++)\n        {\n            for (int j = 0; j <= matrix.GetLength(1) - windowSize; j++)\n            {\n                // Extract the submatrix\n                int[,] subMatrix = GetSubMatrix(matrix, i, j, windowSize);\n\n                // Print the submatrix (for demonstration)\n                PrintMatrix(subMatrix);\n                Console.WriteLine("----");\n            }\n        }\n    }\n\n    static int[,] GetSubMatrix(int[,] matrix, int startRow, int startCol, int size)\n    {\n        int[,] subMatrix = new int[size, size];\n        for (int i = 0; i < size; i++)\n        {\n            for (int j = 0; j < size; j++)\n            {\n                subMatrix[i, j] = matrix[startRow + i, startCol + j];\n            }\n        }\n        return subMatrix;\n    }\n\n    static void PrintMatrix(int[,] matrix)\n    {\n        for (int i = 0; i < matrix.GetLength(0); i++)\n        {\n            for (int j = 0; j < matrix.GetLength(1); j++)\n            {\n                Console.Write(matrix[i, j] + "\t");\n            }\n            Console.WriteLine();\n        }\n    }\n}