using System;\nusing System.Configuration;\n\nnamespace SecureApp\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string dbPassword = Environment.GetEnvironmentVariable("DB_PASSWORD");\n\n            if(string.IsNullOrEmpty(dbPassword))\n            {\n                Console.WriteLine("Database password not set.");\n                return;\n            }\n\n            // Use dbPassword to connect to your database securely\n            Console.WriteLine("Database password retrieved securely.");\n        }\n    }\n}