using Moq;\nusing NUnit.Framework;\n\npublic class TestBaseTests\n{\n    [Test]\n    public void BaseMethod_should_call_correct_child_method()\n    {\n        // Arrange\n        var mockTestBase = new Mock<TestBase>();\n        mockTestBase.CallBase = true; // Ensures that the base class methods are called\n        var testBase = mockTestBase.Object;\n\n        testBase.runMethod1 = true;\n\n        // Act\n        testBase.BaseMethod();\n\n        // Assert\n        mockTestBase.Protected().Verify("ChildMethod1", Times.Once());\n        mockTestBase.Protected().Verify("ChildMethod2", Times.Never());\n    }\n}