// Assuming we have two arrays: times and values\n// Example initialization (you should replace these with actual values)\nfloat times[] = {5.0, 2.0, 9.0, 1.0, 6.0};\nfloat values[] = {50.0, 20.0, 90.0, 10.0, 60.0};\n\nint n = times.length; // Get the length of the array\n\n// Bubble Sort algorithm\nfor (int i = 0; i < n-1; i++) {\n    for (int j = 0; j < n-i-1; j++) {\n        if (times[j] > times[j+1]) {\n            // Swap times[j] and times[j+1]\n            float tempTime = times[j];\n            times[j] = times[j+1];\n            times[j+1] = tempTime;\n\n            // Swap values[j] and values[j+1] to maintain the relation\n            float tempValue = values[j];\n            values[j] = values[j+1];\n            values[j+1] = tempValue;\n        }\n    }\n}\n\n// Now the times array is sorted and the values array has been rearranged accordingly