#include <iostream>\n#include <list>\n\nstruct CMyClass {\n    void *Ptr;\n    int Var;\n\n    bool operator==(const CMyClass &other) const {\n        return Ptr == other.Ptr && Var == other.Var;\n    }\n};\n\nvoid foo(std::list<CMyClass> &myList, const CMyClass &Bad) {\n    myList.remove_if([&Bad](const CMyClass &item) {\n        return item == Bad;\n    });\n}\n\nint main() {\n    std::list<CMyClass> myList = { {nullptr, 1}, {nullptr, 2}, {nullptr, 3}, {nullptr, 1} };\n    CMyClass badItem = {nullptr, 1};\n\n    foo(myList, badItem);\n\n    for(const auto &item : myList) {\n        std::cout << "Ptr: " << item.Ptr << ", Var: " << item.Var << std::endl;\n    }\n\n    return 0;\n}