const int microswitchPin = 2; // Pin connected to microswitch\nconst int lightPin = 13; // Pin connected to light (built-in LED)\n\nvoid setup() {\n  pinMode(microswitchPin, INPUT_PULLUP);\n  pinMode(lightPin, OUTPUT);\n  Serial.begin(9600);\n}\n\nvoid loop() {\n  if (digitalRead(microswitchPin) == LOW) {\n    Serial.println("SWITCH_TRIGGERED");\n    delay(1000); // Debounce delay\n  }\n  \n  if (Serial.available() > 0) {\n    String command = Serial.readStringUntil('\n');\n    if (command == "TURN_ON_LIGHT") {\n      digitalWrite(lightPin, HIGH);\n    } else if (command == "TURN_OFF_LIGHT") {\n      digitalWrite(lightPin, LOW);\n    }\n  }\n}