#include <iostream>\n#include <vector>\n#include <string>\n\nint main() {\n    // Create a vector of 50 strings, each with four characters\n    std::vector<std::string> stringList = {\n        "abcd", "efgh", "ijkl", "mnop", "qrst",\n        "uvwx", "yzab", "cdef", "ghij", "klmn",\n        "opqr", "stuv", "wxyz", "abcd", "efgh",\n        "ijkl", "mnop", "qrst", "uvwx", "yzab",\n        "cdef", "ghij", "klmn", "opqr", "stuv",\n        "wxyz", "abcd", "efgh", "ijkl", "mnop",\n        "qrst", "uvwx", "yzab", "cdef", "ghij",\n        "klmn", "opqr", "stuv", "wxyz", "abcd",\n        "efgh", "ijkl", "mnop", "qrst", "uvwx",\n        "yzab", "cdef", "ghij", "klmn", "opqr"\n    };\n\n    // Loop through each string in the list\n    for (const std::string& str : stringList) {\n        bool containsVowel = false;\n        \n        // Check for the presence of 'o', 'a', or 'e'\n        for (char ch : str) {\n            if (ch == 'o' || ch == 'a' || ch == 'e') {\n                containsVowel = true;\n                break;\n            }\n        }\n\n        // Execute different commands based on whether the letters are found\n        if (containsVowel) {\n            std::cout << "String '" << str << "' contains one of the letters 'o', 'a', or 'e'.\n";\n            // Add your command here for when the string contains one of the letters\n        } else {\n            std::cout << "String '" << str << "' does not contain 'o', 'a', or 'e'.\n";\n            // Add your command here for when the string does not contain one of the letters\n        }\n    }\n\n    return 0;\n}