#include <iostream>\n#include <functional>\n\ntemplate<typename Func>\nvoid superFor(int iterations, Func func) {\n    for (int i = 0; i < iterations; ++i) {\n        func();\n    }\n}\n\nint main() {\n    superFor(3, []() {\n        std::cout << "Executing command" << std::endl;\n        // Add your custom commands here, for example:\n        // add(1 + 2);\n    });\n\n    return 0;\n}