#include <iostream>\n#include <fstream>\n#include <cstdlib> // For rand() and srand()\n#include <ctime>   // For time()\nusing namespace std;\n\nint main() {\n    string start = "enc";  // Initialize start to "enc" to enter the loop\n    char choice;\n    char letter;\n    int x;\n    int y;\n    int z;\n    string filename;\n\n    // Initialize random number generator\n    srand(time(0));\n\n    while (start == "enc") {\n        x = 1;\n        y = 1;\n        cout << "How many garbage characters would you like between each correct character?: ";\n        cin >> z;\n        cout << endl << "Please insert the name of the document you wish to encrypt, make sure you enter the name, and the file type (ie: filename.txt): ";\n        cin >> filename;\n        ifstream infile(filename.c_str());\n        ofstream outfile("encrypted.txt", ios::out);\n\n        // Check if the file was successfully opened\n        if (!infile) {\n            cerr << "Error opening input file!" << endl;\n            return 1;\n        }\n\n        if (!outfile) {\n            cerr << "Error opening output file!" << endl;\n            return 1;\n        }\n\n        while (infile.get(letter)) {  // Corrected the loop to read characters correctly\n            while ((x - y) != z) {\n                outfile << static_cast<char>(33 + rand() % 94);  // Write random garbage character to file\n                x++;\n            }\n            outfile << letter;  // Write the actual character to file\n            y = 1;\n            x = 1;\n        }\n\n        infile.close();\n        outfile.close();\n        cout << endl << "Encryption complete...please return to directory of program, a new file named encrypted.txt will be there." << endl;\n        cout << "Do you wish to try again? Please press y then enter if yes (case sensitive): ";\n        cin >> choice;\n        if (choice != 'y') {\n            break;\n        }\n    }\n\n    return 0;\n}