#include <Windows.h>\n#include <comdef.h>\n#include <iostream>\n\n// Function to get a description of an HRESULT error code\nstd::string GetErrorMessage(HRESULT hr) {\n    _com_error err(hr);\n    LPCTSTR errMsg = err.ErrorMessage();\n    return std::string(errMsg);\n}\n\n// Function to get a more detailed description using FormatMessage\nstd::string GetDetailedErrorMessage(HRESULT hr) {\n    char* errorMsg = nullptr;\n    FormatMessage(\n        FORMAT_MESSAGE_ALLOCATE_BUFFER | FORMAT_MESSAGE_FROM_SYSTEM | FORMAT_MESSAGE_IGNORE_INSERTS,\n        nullptr,\n        hr,\n        MAKELANGID(LANG_NEUTRAL, SUBLANG_DEFAULT),\n        (LPSTR)&errorMsg,\n        0,\n        nullptr\n    );\n\n    std::string message(errorMsg);\n    LocalFree(errorMsg);\n    return message;\n}\n\nint main() {\n    HRESULT hr = E_FAIL; // Example error code\n\n    std::cout << "Error message: " << GetErrorMessage(hr) << std::endl;\n    std::cout << "Detailed error message: " << GetDetailedErrorMessage(hr) << std::endl;\n\n    return 0;\n}