// Forward declaration\nclass MyClass;\n\n// Usage of forward-declared class\nvoid foo(MyClass* ptr);\n\n// Definition of MyClass with overloaded operator&\nclass MyClass {\npublic:\n    MyClass* operator&() {\n        return this; // Example implementation\n    }\n};\n\nvoid foo(MyClass* ptr) {\n    // Do something with ptr\n}\n\nint main() {\n    MyClass obj;\n    foo(&obj); // May cause unexpected behavior due to overloaded operator&\n}