#include <windows.h>\n#include <comdef.h>\n\nint main()\n{\n    // Initialize COM\n    HRESULT hr = CoInitialize(NULL);\n    if (FAILED(hr))\n    {\n        return hr;\n    }\n\n    // Create an instance of the COM object\n    IMyComInterface* pMyComInterface = NULL;\n    hr = CoCreateInstance(CLSID_MyComClass, NULL, CLSCTX_INPROC_SERVER, IID_IMyComInterface, (void**)&pMyComInterface);\n    if (FAILED(hr))\n    {\n        CoUninitialize();\n        return hr;\n    }\n\n    // Use the COM object\n    pMyComInterface->MyMethod();\n\n    // Release the COM object\n    pMyComInterface->Release();\n\n    // Uninitialize COM\n    CoUninitialize();\n    return 0;\n}