#include <windows.h>\n#include <tchar.h>\n#include <stdio.h>\n\nint main() {\n    HKEY hKey;\n    LONG result;\n    TCHAR value[255];\n    DWORD BufferSize = sizeof(value);\n    \n    // Open the registry key with the required permissions\n    result = RegOpenKeyEx(HKEY_LOCAL_MACHINE, _T("SOFTWARE\\Mozilla\\Mozilla Firefox 3.0.10\\bin"), 0, KEY_READ, &hKey);\n    \n    if (result == ERROR_SUCCESS) {\n        // Query the value\n        result = RegQueryValueEx(hKey, _T("Path"), NULL, NULL, (LPBYTE)&value, &BufferSize);\n        \n        if (result == ERROR_SUCCESS) {\n            _tprintf(_T("Firefox path: %s\n"), value);\n        } else {\n            _tprintf(_T("Failed to query the value. Error: %ld\n"), result);\n        }\n        \n        // Close the registry key\n        RegCloseKey(hKey);\n    } else {\n        _tprintf(_T("Failed to open the key. Error: %ld\n"), result);\n    }\n\n    return 0;\n}