#include <windows.h>\n\nint main() {\n    // The path to your file\n    const char* filePath = "C:\\path\\to\\your\\file.txt";\n\n    // Use ShellExecute to open the file\n    HINSTANCE result = ShellExecute(NULL, "open", filePath, NULL, NULL, SW_SHOWNORMAL);\n\n    // Check if the function succeeded\n    if ((int)result <= 32) {\n        // Handle the error\n        MessageBox(NULL, "Failed to open the file.", "Error", MB_ICONERROR | MB_OK);\n    }\n\n    return 0;\n}