#include <windows.h>\n#include <iostream>\n\nvoid RegisterTypeLib(const std::wstring& dllPath) {\n    std::wstring command = L"RegAsm.exe " + dllPath + L" /tlb: " + dllPath + L" /codebase";\n    ShellExecuteW(NULL, L"open", L"cmd.exe", (L"/C " + command).c_str(), NULL, SW_HIDE);\n}\n\nint main() {\n    std::wstring tlbGuid = L"{YOUR-TLB-GUID-HERE}";\n    std::wstring dllPath = L"Path\\To\\Your\\Discovery.dll";\n\n    if (!IsTypeLibRegistered(tlbGuid)) {\n        std::wcout << L"Registering TLB file..." << std::endl;\n        RegisterTypeLib(dllPath);\n    } else {\n        std::wcout << L"TLB file is already registered." << std::endl;\n    }\n\n    return 0;\n}