#include <windows.h>\n#include <iostream>\n\nint main() {\n    HKEY hKey;\n    LPCSTR subKey = "SOFTWARE\\MyApp";\n    LPCSTR valueName = "MyValue";\n    DWORD data = 1234;\n    \n    // Open or create the key\n    LONG result = RegCreateKeyExA(HKEY_CURRENT_USER, subKey, 0, NULL, 0, KEY_WRITE, NULL, &hKey, NULL);\n    if (result == ERROR_SUCCESS) {\n        std::cout << "Registry key opened/created successfully." << std::endl;\n\n        // Set the value\n        result = RegSetValueExA(hKey, valueName, 0, REG_DWORD, (const BYTE*)&data, sizeof(data));\n        if (result == ERROR_SUCCESS) {\n            std::cout << "Registry value set successfully." << std::endl;\n        } else {\n            std::cerr << "Failed to set registry value. Error code: " << result << std::endl;\n        }\n\n        // Close the key\n        RegCloseKey(hKey);\n    } else {\n        std::cerr << "Failed to open/create registry key. Error code: " << result << std::endl;\n    }\n\n    return 0;\n}