#include <iostream>\n#include <fstream>\n#include <sstream>\n#include <string>\n\nusing namespace std;\n\nint main(int arg_count, char *arg_vec[]) {\n    if (arg_count != 2) {\n        cerr << "expected one argument" << endl;\n        return EXIT_FAILURE;\n    }\n\n    string line;\n    ifstream myfile(arg_vec[1]);\n\n    if (myfile.is_open()) {\n        int count = 0;\n        string lastTag = "";\n        string currentTag;\n\n        while (getline(myfile, line)) {\n            stringstream ss(line);\n            ss >> currentTag;\n\n            if (currentTag != lastTag) {\n                if (!lastTag.empty()) {\n                    cout << lastTag << " " << count << endl;\n                }\n                lastTag = currentTag;\n                count = 1;\n            } else {\n                count++;\n            }\n        }\n\n        if (!lastTag.empty()) {\n            cout << lastTag << " " << count << endl;\n        }\n\n        myfile.close();\n    } else {\n        cout << "Unable to open file" << endl;\n    }\n    return 0;\n}