#include <atlbase.h>\n#include <atlcom.h>\n\nint main()\n{\n    // Initialize COM\n    HRESULT hr = CoInitialize(NULL);\n    if (FAILED(hr))\n    {\n        return hr;\n    }\n\n    // Create an instance of the COM object\n    CComPtr<IMyComInterface> spMyComInterface;\n    hr = spMyComInterface.CoCreateInstance(CLSID_MyComClass);\n    if (FAILED(hr))\n    {\n        CoUninitialize();\n        return hr;\n    }\n\n    // Use the COM object\n    spMyComInterface->MyMethod();\n\n    // Uninitialize COM\n    CoUninitialize();\n    return 0;\n}