#include <boost/regex.hpp>\n#include <string>\n#include <iostream>\n#include <sstream>\n\n// Function to convert HTML entity string to a single character\nstd::string decodeEntity(const std::string& entity) {\n    int code;\n    std::istringstream(entity) >> code;\n    return std::string(1, static_cast<char>(code));\n}\n\nint main() {\n    std::string input = "Hello, here's a test colon&#58;. Here's a test semi-colon&#59;";\n    boost::regex entityPattern("&#(\\d+);");\n\n    std::string output;\n    boost::sregex_iterator iter(input.begin(), input.end(), entityPattern);\n    boost::sregex_iterator end;\n\n    std::string::const_iterator lastPos = input.begin();\n    for (; iter != end; ++iter) {\n        output.append(lastPos, iter->prefix().second);\n        output.append(decodeEntity((*iter)[1].str()));\n        lastPos = iter->suffix().first;\n    }\n    output.append(lastPos, input.end());\n\n    std::cout << output << std::endl;\n\n    return 0;\n}