#include <iostream>\n#include <limits.h>\n\nbool lowestPositive(int a, int b, int& result) {\n    if (a <= 0 && b <= 0) {\n        return false; // Neither a nor b is positive\n    }\n    \n    if (a <= 0) {\n        result = b;\n    } else if (b <= 0) {\n        result = a;\n    } else {\n        result = (a < b) ? a : b;\n    }\n    \n    return true;\n}\n\nint main() {\n    int result;\n    if (lowestPositive(5, 6, result)) {\n        std::cout << "The lowest positive number is: " << result << std::endl;\n    } else {\n        std::cout << "Neither number is positive." << std::endl;\n    }\n    \n    return 0;\n}