#include <iostream>\n#include <fstream>\n#include <string>\n\nvoid readLinesFromFile(const std::string& filename, int startLine, int endLine) {\n    std::ifstream file(filename);\n    if (!file.is_open()) {\n        std::cerr << "Could not open the file!" << std::endl;\n        return;\n    }\n\n    std::string line;\n    int currentLine = 1;\n\n    // Skip lines until we reach the startLine\n    while (currentLine < startLine && std::getline(file, line)) {\n        ++currentLine;\n    }\n\n    // Read and print lines from startLine to endLine\n    while (currentLine <= endLine && std::getline(file, line)) {\n        std::cout << line << std::endl;\n        ++currentLine;\n    }\n\n    file.close();\n}\n\nint main() {\n    std::string filename = "example.txt";\n    int startLine = 5;\n    int endLine = 10;\n\n    readLinesFromFile(filename, startLine, endLine);\n\n    return 0;\n}