#include <iostream>\n#include <vector>\n#include <string>\n\nint lorem_ipsum_length(const std::string &text) {\n    std::cout << "Processing text: " << text << std::endl;\n    return text.length();\n}\n\nint main() {\n    std::vector<std::string> words = { "Lorem", "ipsum", "dolor", "sit", "amet", "consectetur", "adipiscing", "elit" };\n    \n    for (const auto &word : words) {\n        int length = lorem_ipsum_length(word);\n        std::cout << "Length of '" << word << "': " << length << std::endl;\n    }\n    \n    return 0;\n}