#include <iostream>\n#include <cstring>\n\nvoid upperCaseString1(char *_str) {\n    for (int i = 0; _str[i] != '\0'; ++i) {\n        _str[i] = toupper(_str[i]);\n    }\n}\n\nvoid upperCaseString2(char _str[]) {\n    for (int i = 0; _str[i] != '\0'; ++i) {\n        _str[i] = toupper(_str[i]);\n    }\n}\n\nint main() {\n    char str1[] = "Hello";\n    char *str2 = new char[6];\n    strcpy(str2, "World");\n\n    upperCaseString1(str1);\n    upperCaseString2(str2);\n\n    std::cout << str1 << std::endl; // Output: HELLO\n    std::cout << str2 << std::endl; // Output: WORLD\n\n    delete[] str2; // Clean up dynamically allocated memory\n    return 0;\n}