#include <iostream>\n#include <mutex>\n\nstd::once_flag initFlag;\n\nvoid initializeCriticalSection() {\n    // Critical section initialization code\n    std::cout << "Critical section initialized." << std::endl;\n}\n\nvoid threadSafeInit() {\n    std::call_once(initFlag, initializeCriticalSection);\n    // Rest of the thread code\n}\n\nint main() {\n    // Example usage with multiple threads\n    std::thread t1(threadSafeInit);\n    std::thread t2(threadSafeInit);\n\n    t1.join();\n    t2.join();\n\n    return 0;\n}