// Add this to your combo box class header file\nafx_msg BOOL OnSetCursor(CWnd* pWnd, UINT nHitTest, UINT message);\n\n// In your combo box class implementation file\nBEGIN_MESSAGE_MAP(CYourComboBoxClass, CComboBox)\n    // Other message handlers...\n    ON_WM_SETCURSOR()\nEND_MESSAGE_MAP()\n\nBOOL CYourComboBoxClass::OnSetCursor(CWnd* pWnd, UINT nHitTest, UINT message)\n{\n    // Set the cursor explicitly\n    ::SetCursor(AfxGetApp()->LoadStandardCursor(IDC_ARROW));\n    return TRUE; // Return TRUE to indicate that you've handled the message\n}