#include <windows.h>\n#include <iostream>\n\nvoid ListFilesInDirectory(const std::wstring& directory) {\n    WIN32_FIND_DATA findFileData;\n    HANDLE hFind = FindFirstFile((directory + L"\\*").c_str(), &findFileData);\n\n    if (hFind == INVALID_HANDLE_VALUE) {\n        std::wcerr << L"FindFirstFile failed (" << GetLastError() << L")\n";\n        return;\n    } \n\n    do {\n        const std::wstring fileOrDirName = findFileData.cFileName;\n        if (fileOrDirName != L"." && fileOrDirName != L"..") {\n            std::wcout << fileOrDirName << std::endl;\n        }\n    } while (FindNextFile(hFind, &findFileData) != 0);\n\n    FindClose(hFind);\n}\n\nint main() {\n    std::wstring directory = L"C:\\path\\to\\directory";\n    ListFilesInDirectory(directory);\n    return 0;\n}