#include <windows.h>\n#include <string>\n#include <iostream>\n#include <sstream>\n\nvoid reportResults() {\n    std::wstring env(_wgetenv(L"ProgramFiles"));\n    env += L"\\Internet Explorer\\iexplore.exe";\n    std::wstringstream url;\n    url << L"\"" << env.c_str() << L"\" \"http://yahoo.com\"";\n    \n    std::wstring command = url.str();\n    STARTUPINFOW si = { sizeof(si) };\n    PROCESS_INFORMATION pi;\n\n    if (!CreateProcessW(\n        nullptr, \n        &command[0], \n        nullptr, \n        nullptr, \n        FALSE, \n        0, \n        nullptr, \n        nullptr, \n        &si, \n        &pi)) {\n        std::wcerr << L"CreateProcess failed (" << GetLastError() << L").\n";\n        return;\n    }\n\n    // Close process and thread handles to avoid memory leaks.\n    CloseHandle(pi.hProcess);\n    CloseHandle(pi.hThread);\n}\n\nint main() {\n    reportResults();\n    return 0;\n}