// porter_stemmer.cpp\n   #include "php.h"\n   #include "porter_stemmer.h" // Your C++ implementation of PorterStemmer\n\n   PHP_FUNCTION(porter_stemmer)\n   {\n       char *input;\n       size_t input_len;\n       if (zend_parse_parameters(ZEND_NUM_ARGS(), "s", &input, &input_len) == FAILURE) {\n           RETURN_FALSE;\n       }\n\n       std::string word(input, input_len);\n       std::string stemmed = PorterStemmer::stem(word);\n\n       RETURN_STRING(stemmed.c_str());\n   }\n\n   const zend_function_entry porter_stemmer_functions[] = {\n       PHP_FE(porter_stemmer, NULL)\n       PHP_FE_END\n   };\n\n   PHP_MINIT_FUNCTION(porter_stemmer)\n   {\n       return SUCCESS;\n   }\n\n   PHP_MSHUTDOWN_FUNCTION(porter_stemmer)\n   {\n       return SUCCESS;\n   }\n\n   PHP_MINFO_FUNCTION(porter_stemmer)\n   {\n       php_info_print_table_start();\n       php_info_print_table_header(2, "porter_stemmer support", "enabled");\n       php_info_print_table_end();\n   }\n\n   zend_module_entry porter_stemmer_module_entry = {\n       STANDARD_MODULE_HEADER,\n       "porter_stemmer",\n       porter_stemmer_functions,\n       PHP_MINIT(porter_stemmer),\n       PHP_MSHUTDOWN(porter_stemmer),\n       NULL,\n       NULL,\n       PHP_MINFO(porter_stemmer),\n       NO_VERSION_YET,\n       STANDARD_MODULE_PROPERTIES\n   };\n\n   #ifdef COMPILE_DL_PORTER_STEMMER\n   ZEND_GET_MODULE(porter_stemmer)\n   #endif