#include <vector>\n#include <algorithm>\n#include <functional>\n#include <cassert>\n\nstruct BitwiseOr {\n    char operator()(const char& a, const char& b) const { return a | b; }\n};\n\nvoid orTogether(std::vector<char>& v1, const std::vector<char>& v2) {\n    assert(v1.size() == v2.size()); // Ensure vectors are of the same length\n    std::transform(v1.begin(), v1.end(), v2.begin(), v1.begin(), BitwiseOr());\n}