// main.cpp\n#include <iostream>\n#include <vcclr.h>\n#include "MyWrapper.h"\n\nint main()\n{\n    // Initialize the CLR\n    gcroot<MyWrapper^> wrapper = gcnew MyWrapper();\n\n    // Call the C# method\n    System::String^ result = wrapper->SayHello("World");\n    \n    // Convert System::String^ to std::string\n    std::string resultStr;\n    {\n        using namespace System::Runtime::InteropServices;\n        const char* chars = (const char*)(Marshal::StringToHGlobalAnsi(result)).ToPointer();\n        resultStr = chars;\n        Marshal::FreeHGlobal(IntPtr((void*)chars));\n    }\n\n    std::cout << resultStr << std::endl;\n    return 0;\n}