#include <Windows.h>\n#include <iostream>\n\nint main() {\n    // Initialize COM library\n    HRESULT hr = CoInitialize(NULL);\n    if (FAILED(hr)) {\n        std::cerr << "Failed to initialize COM library" << std::endl;\n        return 1;\n    }\n\n    // Allocate memory using CoTaskMemAlloc\n    SIZE_T size = 100 * sizeof(char); // Allocate memory for 100 characters\n    char* pMemory = (char*)CoTaskMemAlloc(size);\n    if (pMemory == NULL) {\n        std::cerr << "Memory allocation failed" << std::endl;\n        CoUninitialize();\n        return 1;\n    }\n\n    // Use the allocated memory\n    strcpy_s(pMemory, size, "Hello, COM memory allocation!");\n\n    // Print the allocated memory content\n    std::cout << "Allocated memory content: " << pMemory << std::endl;\n\n    // Free the allocated memory using CoTaskMemFree\n    CoTaskMemFree(pMemory);\n\n    // Uninitialize COM library\n    CoUninitialize();\n\n    return 0;\n}