#include <iostream>\n#include <windows.h>\n\ntypedef int(__stdcall *pAddNumbers)(int, int);\n\nint main()\n{\n    HINSTANCE hDLL = LoadLibrary(L"MyWin32Dll.dll");\n    if (hDLL != NULL)\n    {\n        pAddNumbers AddNumbers = (pAddNumbers)GetProcAddress(hDLL, "AddNumbers");\n        if (AddNumbers != NULL)\n        {\n            int result = AddNumbers(3, 5);\n            std::cout << "Result: " << result << std::endl;\n        }\n        FreeLibrary(hDLL);\n    }\n    else\n    {\n        std::cerr << "Failed to load DLL" << std::endl;\n    }\n\n    return 0;\n}