#include <sys/time.h>\n#include <iostream>\n\nusing namespace std;\n\nint main()\n{\n    hrtime_t start;\n    hrtime_t tmp;\n    hrtime_t fin;\n\n    start = gethrtime();\n    tmp = start / 1000000; // Integer division\n    fin = gethrtime();\n\n    cout << "Original val: " << start << endl;\n    cout << "Computed: " << tmp << endl;\n    cout << "Time: " << fin - start << endl;\n\n    return 0;\n}