#include <iostream>\n#include <boost/asio.hpp>\n#include <boost/asio/ssl.hpp>\n\nbool checkMailServerStatus(const std::string& server, const std::string& port) {\n    try {\n        boost::asio::io_context io_context;\n        boost::asio::ssl::context ssl_context(boost::asio::ssl::context::sslv23);\n        boost::asio::ssl::stream<boost::asio::ip::tcp::socket> socket(io_context, ssl_context);\n\n        boost::asio::ip::tcp::resolver resolver(io_context);\n        boost::asio::connect(socket.lowest_layer(), resolver.resolve(server, port));\n        socket.handshake(boost::asio::ssl::stream_base::client);\n\n        return true; // Connection successful\n    } catch (const std::exception& e) {\n        std::cerr << "Error: " << e.what() << '\n';\n        return false; // Connection failed\n    }\n}\n\nint main() {\n    std::string server = "smtp.example.com";\n    std::string port = "465"; // SMTP over SSL\n\n    if (checkMailServerStatus(server, port)) {\n        std::cout << "Mail server is up and reachable.\n";\n    } else {\n        std::cout << "Mail server is down or unreachable.\n";\n    }\n\n    return 0;\n}