#include <windows.h>\n#include <iostream>\n\nvoid SetConsoleWidth(int width) {\n    HANDLE hConsole = GetStdHandle(STD_OUTPUT_HANDLE);\n    if (hConsole == INVALID_HANDLE_VALUE) {\n        std::cerr << "Error getting console handle" << std::endl;\n        return;\n    }\n\n    CONSOLE_SCREEN_BUFFER_INFO bufferInfo;\n    if (!GetConsoleScreenBufferInfo(hConsole, &bufferInfo)) {\n        std::cerr << "Error getting console buffer info" << std::endl;\n        return;\n    }\n\n    COORD newSize;\n    newSize.X = width;\n    newSize.Y = bufferInfo.dwSize.Y;\n\n    if (!SetConsoleScreenBufferSize(hConsole, newSize)) {\n        std::cerr << "Error setting console buffer size" << std::endl;\n        return;\n    }\n\n    SMALL_RECT newRect = bufferInfo.srWindow;\n    newRect.Right = newRect.Left + width - 1;\n\n    if (!SetConsoleWindowInfo(hConsole, TRUE, &newRect)) {\n        std::cerr << "Error setting console window info" << std::endl;\n        return;\n    }\n}\n\nint main() {\n    // Set console width to 120 characters\n    SetConsoleWidth(120);\n    std::cout << "Console width changed to 120 characters." << std::endl;\n    return 0;\n}