#include <QCoreApplication>\n#include <QNetworkAccessManager>\n#include <QNetworkRequest>\n#include <QNetworkReply>\n#include <QAuthenticator>\n#include <QUrl>\n#include <QObject>\n\nclass MyApp : public QObject {\n    Q_OBJECT\n\npublic:\n    MyApp(QObject *parent = nullptr) : QObject(parent) {\n        connect(&networkManager, &QNetworkAccessManager::proxyAuthenticationRequired,\n                this, &MyApp::provideAuthentication);\n\n        QNetworkRequest request(QUrl("http://example.com"));\n        networkManager.get(request);\n    }\n\nprivate slots:\n    void provideAuthentication(const QNetworkProxy &proxy, QAuthenticator *authenticator) {\n        authenticator->setUser("your_username");\n        authenticator->setPassword("your_password");\n    }\n\nprivate:\n    QNetworkAccessManager networkManager;\n};\n\nint main(int argc, char *argv[]) {\n    QCoreApplication app(argc, argv);\n\n    MyApp myApp;\n\n    return app.exec();\n}\n\n#include "main.moc"