#include <windows.h>\n#include <string>\n#include <iostream>\n\nstd::string GetWindowTitle(HWND hwnd) {\n    char title[256];\n    GetWindowText(hwnd, title, sizeof(title));\n    return std::string(title);\n}\n\nint main() {\n    HWND hwnd = FindWindow(NULL, "Untitled - Notepad"); // Example: Find the Notepad window\n    if (hwnd) {\n        std::string title = GetWindowTitle(hwnd);\n        std::cout << "Window Title: " << title << std::endl;\n    } else {\n        std::cerr << "Window not found!" << std::endl;\n    }\n    return 0;\n}