#include <windows.h>\n#include <ras.h>\n#include <raserror.h>\n#include <iostream>\n\n#pragma comment(lib, "rasapi32.lib")\n\nint main() {\n    HRASCONN hRasConn = NULL;\n    RASCONN rasConn;\n    rasConn.dwSize = sizeof(RASCONN);\n    DWORD dwSize = sizeof(RASCONN);\n    DWORD dwConnections = 0;\n    \n    // Enumerate the connections\n    if (RasEnumConnections(&rasConn, &dwSize, &dwConnections) == ERROR_SUCCESS) {\n        if (dwConnections > 0) {\n            hRasConn = rasConn.hrasconn;\n\n            RAS_STATS rasStats;\n            ZeroMemory(&rasStats, sizeof(RAS_STATS));\n            rasStats.dwSize = sizeof(RAS_STATS);\n\n            // Get connection statistics\n            if (RasGetConnectionStatistics(hRasConn, &rasStats) == ERROR_SUCCESS) {\n                // The total bytes sent and received\n                DWORD totalBytes = rasStats.dwBytesXmited + rasStats.dwBytesRcved;\n                DWORD totalKBytes = totalBytes / 1024;\n\n                std::cout << "Total GPRS usage: " << totalKBytes << " KB" << std::endl;\n            } else {\n                std::cerr << "Failed to get connection statistics" << std::endl;\n            }\n        } else {\n            std::cerr << "No active connections found" << std::endl;\n        }\n    } else {\n        std::cerr << "Failed to enumerate connections" << std::endl;\n    }\n\n    return 0;\n}