// File: DLLb.cpp\n\n#include <windows.h>\n#include <iostream>\n\n// Forward declaration of functions in DLL A\nextern "C" void fooA();\n\nextern "C" __declspec(dllexport) void fooB() {\n    fooA();\n}\n\nextern "C" __declspec(dllexport) void fooBB() {\n}\n\n// Entry point for DLL\nBOOL APIENTRY DllMain(HMODULE hModule, DWORD  ul_reason_for_call, LPVOID lpReserved) {\n    switch (ul_reason_for_call) {\n    case DLL_PROCESS_ATTACH:\n    case DLL_THREAD_ATTACH:\n    case DLL_THREAD_DETACH:\n    case DLL_PROCESS_DETACH:\n        break;\n    }\n    return TRUE;\n}